CREATE TABLE `fe_edge_device` (
                                  `edge_device_id` bigint NOT NULL AUTO_INCREMENT COMMENT '网关设备ID',
                                  `edge_device_name` varchar(150) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci NOT NULL COMMENT '网关设备名称',
                                  `device_desc` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT '设备描述',
                                  `device_position` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT '设备位置',
                                  `ip_address` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT '设备IP地址',
                                  `edge_group_id` bigint DEFAULT '0' COMMENT '设备组ID',
                                  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
                                  `create_by` bigint DEFAULT '0' COMMENT '创建者ID',
                                  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
                                  `update_by` bigint DEFAULT '0' COMMENT '更新者ID',
                                  `edge_cloud_id` bigint DEFAULT '0' COMMENT '所属边缘云id',
                                  `serial_num` varchar(100) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT '网关设备序列号',
                                  `tenant_id` bigint DEFAULT '0' COMMENT '设备所属租户ID',
                                  `area` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT '所属区域',
                                  `mac` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT 'mac地址',
                                  `status` tinyint(1) DEFAULT '0' COMMENT '在线/离线',
                                  `report_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '上报时间',
                                  `is_deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否待删除 0----否 ，1-----是',
                                  `sim_card` varchar(100) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '',
                                  `node_port` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT '节点端口',
                                  `type` int NOT NULL DEFAULT '1' COMMENT '1----网关  2-----节点',
                                  `authorization_code` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '授权码',
                                  `is_effective` tinyint DEFAULT '0' COMMENT '授权码是否生效 0----否 1----是',
                                  `is_kong` tinyint DEFAULT '1' COMMENT '是否调用Kong  0----否 1----是',
                                  `gateway_id` bigint DEFAULT '0' COMMENT '网关型号id',
                                  `project_id` bigint DEFAULT '0' COMMENT '所属项目id',
                                  `device_sn` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT '设备SN',
                                  `device_location` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT '空间标识',
                                  `gateway_type` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT '' COMMENT '网关型号',
                                  `gateway_category` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT '网关类别',
                                  `sim_ip_address` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT '' COMMENT 'sim卡ip地址',
                                  PRIMARY KEY (`edge_device_id`) USING BTREE,
                                  UNIQUE KEY `uniq_idx_fe_edge_device_mac` (`mac`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_unicode_ci ROW_FORMAT=DYNAMIC COMMENT='网关设备表'