CREATE TABLE `ne_device` (
                             `nid` bigint NOT NULL AUTO_INCREMENT COMMENT '设备ID',
                             `device_id` varchar(50) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '设备ID',
                             `device_name` varchar(100) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '设备名称',
                             `addr_protocol` varchar(20) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '地址协议',
                             `connect_protocol` varchar(50) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '访问协议：MODBUS_TCP, MODBUS_RTU, OPCUA, HTTP, MTCONNECT, MITSUBISH_MC, SIEMENS_S7, OMRON_SINS',
                             `admin_state` varchar(20) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '管理状态：LOCKED-已锁定,UNLOCKED-正常',
                             `operating_state` varchar(20) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '操作状态：enabled-正常,disabled-禁用',
                             `device_labels` varchar(512) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '设备标签',
                             `device_locations` varchar(100) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '设备位置',
                             `addr_id` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '设备地址ID(暂不使用)',
                             `device_service_name` varchar(50) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT 'flexedgex-device-service' COMMENT '设备服务名称',
                             `profile_nid` bigint NOT NULL COMMENT '设备配置文件ID',
                             `agw_id` bigint NOT NULL COMMENT '网关ID',
                             `tenant_id` bigint DEFAULT '0' COMMENT '租户ID',
                             `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '设备创建时间',
                             `create_by` bigint DEFAULT '0' COMMENT '设备创建人',
                             `issued` tinyint(1) DEFAULT '0' COMMENT '是否已经下发：1-是，0-否',
                             `issue_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP  COMMENT '下发时间',
                             `device_status` tinyint(1) DEFAULT '0' COMMENT '0---离线 1---- 在线',
                             `device_desc` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '设备描述',
                             `device_encoding` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '设备编码',
                             `device_model` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '设备型号',
                             PRIMARY KEY (`nid`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8mb3 ROW_FORMAT=DYNAMIC COMMENT='设备信息'